mapper.select:
select ad.apt_deal_id, ad.floor, ad.area, ad.deal_date, ad.price, m.nickname from apt_deal ad left join member m on m.member_id = ad.member_id where ad.apt_id = #{houseId}
and ad.apt_deal_id > #{lastDealId}
order by ad.apt_deal_id asc limit #{size};

select apt.apt_id, apt.name, apt.lat, apt.lng, apt.built_year, apt.jibun, dong.dong_code, dong.sido, dong.gugun, dong.dong, ifnull(max(apt_deal.price), 0) as max_price, ifnull(min(apt_deal.price), 0) as min_price from apt left join dong on apt.dong_code = dong.dong_code left join apt_deal on apt.apt_id = apt_deal.apt_id where apt.dong_code = #{dongCode}
and apt.apt_id > #{lastHouseId}
group by apt.apt_id order by apt.apt_id asc limit #{size};

select distinct left(dong_code, 5) dong_code, sido, gugun
from dong
where left(dong_code, 2) = #{dongCode}
  and gugun is not null;

select dong_code, sido, gugun, dong
from dong
where left(dong_code, 5) = #{dongCode}
  and dong is not null;
